const pub = $json;

// Check if we already collected metrics today for this publication
// Build the video IDs for batch request
return [{
  json: {
    ...pub,
    youtube_video_id: pub.id_publicacao_plataforma
  }
}];

const pubData = $('loop-by-publication').item.json;
const ytResponse = $json;

const stats = ytResponse.items?.[0]?.statistics || {};

const views = parseInt(stats.viewCount || '0', 10);
const likes = parseInt(stats.likeCount || '0', 10);
const comments = parseInt(stats.commentCount || '0', 10);
// YouTube API doesn't expose shares directly
const shares = 0;

return [{
  json: {
    publicacao_id: pubData.publicacao_id,
    plataforma: pubData.plataforma,
    views: views,
    likes: likes,
    comentarios: comments,
    compartilhamentos: shares,
    titulo_final: pubData.titulo_final,
    engine_usada: pubData.engine_usada,
    nicho: pubData.nicho,
    duracao_segundos: pubData.duracao_segundos,
    canal_id: pubData.canal_id,
    engagement_rate: views > 0 ? (((likes + comments) / views) * 100).toFixed(2) : '0.00',
    has_stats: views > 0 || likes > 0
  }
}];

const today = new Date();
const dayOfWeek = today.getDay();
// Run weekly insights on Sunday (0) or Monday (1)
const isWeeklyDay = dayOfWeek === 1;

return [{
  json: {
    ...$json,
    is_weekly: isWeeklyDay,
    current_day: dayOfWeek
  }
}];

const rows = $input.all().map(item => item.json);

const tops = rows.filter(r => r.categoria === 'top');
const flops = rows.filter(r => r.categoria === 'flop');

const formatRow = (r) => 
  `- "${r.titulo_final}" | nicho: ${r.nicho} | engine: ${r.engine_usada} | views: ${r.views} | likes: ${r.likes} | comments: ${r.comentarios} | engagement: ${r.engagement_rate}% | duracao: ${Math.round((r.duracao_segundos || 0) / 60)}min`;

const report = `## RELATÓRIO SEMANAL DE PERFORMANCE\n\n### TOP 10 (mais views):\n${tops.map(formatRow).join('\n')}\n\n### FLOP 10 (menos views):\n${flops.map(formatRow).join('\n')}`;

return [{ json: { report, total_analyzed: rows.length } }];

const aiRaw = $input.first().json;
const report = $('build-weekly-report').item.json.report;

let insights = {};
try {
  if (aiRaw.text) {
    insights = JSON.parse(aiRaw.text.replace(/```json\n?|```\n?/g, '').trim());
  } else if (aiRaw.output) {
    insights = typeof aiRaw.output === 'string' ? JSON.parse(aiRaw.output) : aiRaw.output;
  } else if (aiRaw.resumo_geral) {
    insights = aiRaw;
  }
} catch (e) {
  insights = {
    resumo_geral: 'Erro ao processar insights',
    recomendacoes: []
  };
}

const sanitize = (s) => String(s || '').replace(/'/g, "''");

return [{
  json: {
    insights_json: sanitize(JSON.stringify(insights)),
    resumo_geral: sanitize(insights.resumo_geral || ''),
    nichos_promissores: sanitize(JSON.stringify(insights.nichos_promissores || [])),
    recomendacoes: sanitize(JSON.stringify(insights.recomendacoes || [])),
    score_semana: insights.score_semana || 0,
    report_raw: sanitize(report)
  }
}];
